void bsdl_flex_switch_file(yyscan_t scanner, char *filename)
{
  scan_extra_t *extra;
  FILE *f;
  char *s;

  /* convert filename to all upper case */
  s = filename;
  while (*s) {
    if (islower(*s))
      *s = toupper(*s);
    s++;
  }

  /* file in current directory has precedence */
  f = fopen(filename, "r");
  if (!f) {
    char db_path[] = JTAG_DATA_DIR;
    char *db_file;

    if ((db_file = (char *)malloc(strlen(db_path) +
                                  1 +  /* "/"    */
                                  4 +  /* "bsdl" */
                                  1 +  /* "/"    */
                                  strlen(filename) +
                                  1))) {
      strcpy(db_file, db_path);
      strcat(db_file, "/");
      strcat(db_file, "bsdl");
      strcat(db_file, "/");
      strcat(db_file, filename);
      f = fopen(db_file, "r");

      if (!f)
        bsdl_msg(BSDL_MSG_FATAL, _("Cannot open file %s or %s.\n"), filename, db_file);
      free(db_file);
      if (!f)
        return;
    }
  }

  bsdlpush_buffer_state(bsdl_create_buffer(f,YY_BUF_SIZE,scanner),scanner);
  extra = bsdlget_extra(scanner);
  bsdlset_lineno(1,scanner);
}

void bsdl_flex_switch_buffer(yyscan_t scanner, const char *buffer)
{
  /* ugly, ulgy, ugly
     prepare yyg for later use of YY_CURRENT_BUFFER */
  struct yyguts_t * yyg = (struct yyguts_t*)scanner;
  int lineno;

  lineno = bsdlget_lineno(scanner);
  /* bsdl_scan_string() switches to the string buffer internally,
     so we must save the current buffer state explicitly by pushing the stack
     and setting top of stack to the current buffer state again.
     bsdl_scan_string() can then savely switch YY_CURRENT_BUFFER to the string buffer.
     bsdlpop_buffer_state() will delete the string buffer afterwards and pop the saved
     current buffer state. */
  bsdlpush_buffer_state(YY_CURRENT_BUFFER,scanner);
  bsdl_scan_string(buffer,scanner);
  bsdlset_lineno(lineno,scanner);
}

